mapper.select:
select c.c_Id as "cId", c.u_Id as "uId", c.g_Code as "gCode", c.c_Amount as "cAmount", g.g_Name as "gName", g.g_Price as "gPrice" 
from CART c inner join goods g on c.g_Code = g.g_Code 
where c.c_Id = #{cId}

select c.c_Id as "cId", c.u_Id as "uId", c.g_Code as "gCode", c.c_Amount as "cAmount", g.g_Name as "gName", g.g_Price as "gPrice" 
 from CART c inner join goods g on c.g_Code = g.g_Code 
 where c.c_Id = #{cId}

select c.c_Id as "cId", c.u_Id as "uId", c.g_Code as "gCode", c.c_Amount as "cAmount", g.g_Name as "gName", g.g_Price as "gPrice" 
from CART c inner join goods g on c.g_Code = g.g_Code 
where c.u_Id = #{uId}

select count(*)
 from cart
 where u_Id = #{uId}
 and g_Code = #{gCode}
